INSERT INTO `goods_info` VALUES (1071, NULL, '元代青花官窑云龙纹罐', '20cm*22cm*1个,青花瓷器,元代,20w-30w', '<p>编号：03-108</p><p>尺寸：20cm*22cm*1个</p><p>材质：青花瓷器</p><p>年号： 元代</p><p>价格： 20w-30w</p><p>简介：</p><p><span style=\"font-family: arial, helvetica,sans-serif; font-size: 16px;\"><span style=\"font-family: arial, helvetica,sans-serif;\">元青花云龙纹文字罐：罐体正面所画是青花鱼藻纹，背面青花行书：</span>“大元国至顺庚辰奉八月初八日、御窑特别钦赐翰林大学士、兵部尚书、御赐世袭一等候赵孟頫，中府上公用”；罐之下部绘青花宝相花纹，青花发色深沉、湛蓝，线条勾画的笔触中间和大笔涂抹的驻笔处，有渗入胎骨的凹陷和铁锈斑。经查赵孟頫年谱，元明宗继位后以示恩典，“特别钦赐”给了赵家一些御窑瓷器。</span></p><p><br/></p>', 0, NULL, NULL, 8, 1);
